import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { WebSocketServer, WebSocket } from "ws";
import { storage } from "./storage";
import { insertSessionSchema, insertParticipantSchema, insertItemClaimSchema } from "@shared/schema";
import { z } from "zod";
import { getIbanFromTink, exchangeCodeForToken } from "./tink-integration.js";
import { MockBankService } from "./mock-bank-service.js";

interface WebSocketClient extends WebSocket {
  sessionId?: string;
}

export async function registerRoutes(app: Express): Promise<Server> {
  const httpServer = createServer(app);

  // WebSocket server
  const wss = new WebSocketServer({ server: httpServer, path: '/ws' });

  const sessionClients = new Map<string, Set<WebSocketClient>>();

  wss.on('connection', (ws: WebSocketClient) => {
    console.log('WebSocket connection established');

    ws.on('message', (message) => {
      try {
        const data = JSON.parse(message.toString());

        if (data.type === 'join-session' && data.sessionId) {
          ws.sessionId = data.sessionId;

          if (!sessionClients.has(data.sessionId)) {
            sessionClients.set(data.sessionId, new Set());
          }
          sessionClients.get(data.sessionId)!.add(ws);

          console.log(`Client joined session ${data.sessionId}`);
        }
      } catch (error) {
        console.error('WebSocket message error:', error);
      }
    });

    ws.on('close', () => {
      if (ws.sessionId) {
        const clients = sessionClients.get(ws.sessionId);
        if (clients) {
          clients.delete(ws);
          if (clients.size === 0) {
            sessionClients.delete(ws.sessionId);
          }
        }
      }
    });
  });

  // Broadcast to session participants
  function broadcastToSession(sessionId: string, message: any) {
    const clients = sessionClients.get(sessionId);
    if (clients) {
      clients.forEach(client => {
        if (client.readyState === WebSocket.OPEN) {
          client.send(JSON.stringify(message));
        }
      });
    }
  }

  // Get client configuration
  app.get('/api/config', (req, res) => {
    // Generate the correct redirect URI based on the current request
    const protocol = req.headers['x-forwarded-proto'] || (req.secure ? 'https' : 'http');
    const host = req.headers.host;
    const redirectUri = `${protocol}://${host}/auth/tink/callback`;

    res.json({
      tinkClientId: process.env.TINK_CLIENT_ID?.trim(),
      tinkRedirectUri: redirectUri
    });
  });

  // Get mock bill data from QR scan
  app.post('/api/scan-qr', async (req, res) => {
    try {
      const { tableNumber, restaurantName } = req.body;

      if (!tableNumber || !restaurantName) {
        return res.status(400).json({ message: 'Table number and restaurant name required' });
      }

      const billData = await storage.getMockBillData(tableNumber, restaurantName);

      if (!billData) {
        return res.status(404).json({ message: 'Bill not found for this table' });
      }

      res.json(billData);
    } catch (error) {
      console.error('QR scan error:', error);
      res.status(500).json({ message: 'Failed to retrieve bill data' });
    }
  });

  // Create new session
  app.post('/api/sessions', async (req, res) => {
    try {
      console.log('POST /api/sessions - Raw request body:', JSON.stringify(req.body, null, 2));

      const { userData, billItems, ...sessionFields } = req.body;

      console.log('Extracted sessionFields:', sessionFields);
      console.log('userData:', userData);
      console.log('billItems length:', billItems?.length);

      // Extract participant count from userData
      const participantCount = userData?.participantCount || 4;

      const sessionDataForValidation = {
        ...sessionFields,
        participantCount
      };

      console.log('Session data before validation:', sessionDataForValidation);

      const sessionData = insertSessionSchema.parse(sessionDataForValidation);

      console.log('Session data after validation:', sessionData);

      const session = await storage.createSession(sessionData);

      // Create bill items if provided
      if (billItems && billItems.length > 0) {
        const itemsWithSession = billItems.map((item: any) => ({
          ...item,
          sessionId: session.id
        }));
        await storage.createBillItems(itemsWithSession);
      }

      // Create main booker participant if userData provided
      if (userData?.name) {
        const mainBookerData = {
          sessionId: session.id,
          name: userData.name,
          bankAccount: userData.bankAccount || '',
          isMainBooker: true,
          hasPaid: false,
          paidAmount: '0',
          expectedAmount: '0'
        };

        const mainBooker = await storage.createParticipant(mainBookerData);

        // Update session with main booker ID
        await storage.updateSession(session.id, { mainBookerId: mainBooker.id });

        // Handle main booker's item selection for 'items' mode
        let expectedAmount = 0;
        if (session.splitMode === 'items' && userData.selectedItems && billItems?.length > 0) {

          // Get the created bill items from database first
          const createdItems = await storage.getBillItemsBySession(session.id);

          // Create item claims for main booker's selections
          for (const selection of userData.selectedItems) {
            const billItem = billItems[selection.index];
            if (billItem && selection.quantity > 0) {
              // Find matching item in database
              const matchingItem = createdItems.find(item => 
                item.name === billItem.name && 
                item.price === billItem.price
              );

              if (matchingItem) {
                // Create item claim with the actual selected quantity
                await storage.createItemClaim({
                  participantId: mainBooker.id,
                  billItemId: matchingItem.id,
                  quantity: selection.quantity
                });

                expectedAmount += parseFloat(matchingItem.price) * selection.quantity;
              }
            }
          }

          // Update main booker's expected amount based on selected items
          await storage.updateParticipant(mainBooker.id, {
            expectedAmount: expectedAmount.toFixed(2)
          });
        }

        // Auto-mark main booker as paid since they pay restaurant directly
        const totalAmount = parseFloat(session.totalAmount);

        // For items mode, preserve the expectedAmount that was already set based on selected items
        // For equal mode, set the expectedAmount to the equal share based on actual participant count
        const mainBookerExpectedAmount = session.splitMode === 'equal' 
          ? (totalAmount / (participantCount || 1)).toString() 
          : expectedAmount.toFixed(2); // Use the calculated amount from selected items

        await storage.updateParticipant(mainBooker.id, {
          hasPaid: true,
          paidAmount: totalAmount.toString(),
          expectedAmount: mainBookerExpectedAmount
        });

        // Create payment record for main booker
        await storage.createPayment({
          sessionId: session.id,
          participantId: mainBooker.id,
          amount: totalAmount.toString(),
          status: 'completed'
        });
      }

      console.log('Session created successfully:', session);
      res.json(session);
    } catch (error) {
      console.error('Create session error:', error);

      // More detailed error reporting
      if (error instanceof Error) {
        console.error('Error message:', error.message);
        console.error('Error stack:', error.stack);
      }

      res.status(400).json({ 
        message: 'Invalid session data',
        error: error instanceof Error ? error.message : String(error)
      });
    }
  });

  // Get session details
  app.get('/api/sessions/:id', async (req, res) => {
    try {
      const session = await storage.getSession(req.params.id);

      if (!session) {
        return res.status(404).json({ message: 'Session not found' });
      }

      const participants = await storage.getParticipantsBySession(session.id);
      const billItems = await storage.getBillItemsBySession(session.id);
      const itemClaims = await storage.getItemClaimsBySession(session.id);
      const payments = await storage.getPaymentsBySession(session.id);

      res.json({
        session,
        participants,
        billItems,
        itemClaims,
        payments
      });
    } catch (error) {
      console.error('Get session error:', error);
      res.status(500).json({ message: 'Failed to retrieve session' });
    }
  });

  // Create bill items for session
  app.post('/api/sessions/:id/items', async (req, res) => {
    try {
      const { items } = req.body;
      const sessionId = req.params.id;

      const itemsWithSession = items.map((item: any) => ({
        ...item,
        sessionId
      }));

      const createdItems = await storage.createBillItems(itemsWithSession);

      broadcastToSession(sessionId, {
        type: 'items-updated',
        items: createdItems
      });

      res.json(createdItems);
    } catch (error) {
      console.error('Create bill items error:', error);
      res.status(400).json({ message: 'Failed to create bill items' });
    }
  });

  // Join session as participant
  app.post('/api/sessions/:id/join', async (req, res) => {
    try {
      const sessionId = req.params.id;
      const session = await storage.getSession(sessionId);

      if (!session) {
        return res.status(404).json({ message: 'Session not found' });
      }

      const participantData = insertParticipantSchema.parse({
        ...req.body,
        sessionId
      });

      const participant = await storage.createParticipant(participantData);

      // For equal split mode, recalculate expected amounts for all participants
      if (session.splitMode === 'equal') {
        const allParticipants = await storage.getParticipantsBySession(sessionId);
        const totalAmount = parseFloat(session.totalAmount);
        const participantCount = allParticipants.length;
        const expectedAmountPerPerson = (totalAmount / participantCount).toFixed(2);

        // Update expected amount for all participants
        for (const p of allParticipants) {
          await storage.updateParticipant(p.id, {
            expectedAmount: expectedAmountPerPerson
          });
        }
      }

      broadcastToSession(sessionId, {
        type: 'participant-joined',
        participant
      });

      res.json(participant);
    } catch (error) {
      console.error('Join session error:', error);
      res.status(400).json({ message: 'Failed to join session' });
    }
  });

  // Claim items (Pay Your Part mode)
  app.post('/api/sessions/:id/claim-items', async (req, res) => {
    try {
      const { participantId, itemClaims } = req.body;
      const sessionId = req.params.id;

      // Delete existing claims for this participant
      const existingClaims = await storage.getItemClaimsBySession(sessionId);
      for (const claim of existingClaims) {
        if (claim.participantId === participantId) {
          await storage.deleteItemClaim(claim.participantId, claim.billItemId);
        }
      }

      // Create new claims
      const createdClaims = [];
      for (const claim of itemClaims) {
        const newClaim = await storage.createItemClaim({
          participantId,
          billItemId: claim.billItemId,
          quantity: claim.quantity
        });
        createdClaims.push(newClaim);
      }

      // Calculate expected amount for participant
      const billItems = await storage.getBillItemsBySession(sessionId);
      let expectedAmount = 0;

      for (const claim of createdClaims) {
        const item = billItems.find(i => i.id === claim.billItemId);
        if (item) {
          expectedAmount += parseFloat(item.price) * claim.quantity;
        }
      }

      await storage.updateParticipant(participantId, {
        expectedAmount: expectedAmount.toFixed(2)
      });

      broadcastToSession(sessionId, {
        type: 'items-claimed',
        participantId,
        claims: createdClaims,
        expectedAmount
      });

      res.json({ claims: createdClaims, expectedAmount });
    } catch (error) {
      console.error('Claim items error:', error);
      res.status(400).json({ message: 'Failed to claim items' });
    }
  });

  // Process payment (for participants paying to main booker)
  app.post('/api/sessions/:id/pay', async (req, res) => {
    try {
      const { participantId, amount } = req.body;
      const sessionId = req.params.id;

      // Get session and main booker info
      const session = await storage.getSession(sessionId);
      const participants = await storage.getParticipantsBySession(sessionId);
      const mainBooker = participants.find(p => p.isMainBooker);

      if (!session || !mainBooker) {
        return res.status(404).json({ message: 'Session or main booker not found' });
      }

      // Create payment record (participant pays to main booker's account)
      const payment = await storage.createPayment({
        sessionId,
        participantId,
        amount: amount.toString(),
        status: 'completed' // Simulated banking payment always succeeds
      });

      // Update participant as paid
      await storage.updateParticipant(participantId, {
        hasPaid: true,
        paidAmount: amount.toString()
      });

      // Broadcast real-time update to all session participants
      broadcastToSession(sessionId, {
        type: 'participant-payment-completed',
        participantId,
        participant: await storage.getParticipant(participantId),
        payment
      });

      // Check if all participants have paid
      const updatedParticipants = await storage.getParticipantsBySession(sessionId);
      const allPaid = updatedParticipants.every(p => p.hasPaid);

      if (allPaid) {
        await storage.updateSession(sessionId, { isActive: false });
        broadcastToSession(sessionId, {
          type: 'session-completed'
        });
      }

      res.json(payment);
    } catch (error) {
      console.error('Payment error:', error);
      res.status(400).json({ message: 'Payment failed' });
    }
  });

  // Initiate banking payment flow  
  app.post('/api/sessions/:id/initiate-payment', async (req, res) => {
    try {
      const sessionId = req.params.id;

      // Get session data
      const session = await storage.getSession(sessionId);
      if (!session) {
        return res.status(404).json({ message: 'Session not found' });
      }

      const participants = await storage.getParticipantsBySession(sessionId);
      const mainBooker = participants.find(p => p.isMainBooker);

      if (!mainBooker) {
        return res.status(400).json({ message: 'Main booker not found' });
      }

      // Return payment initialization data
      res.json({
        success: true,
        sessionId,
        amount: session.totalAmount,
        recipient: session.restaurantName,
        description: `${session.restaurantName} - Tafel ${session.tableNumber}`,
        mainBooker: mainBooker.name
      });

    } catch (error) {
      console.error('Payment initiation error:', error);
      res.status(500).json({ message: 'Failed to initiate payment' });
    }
  });

  // Complete banking payment (called after successful banking flow)
  app.post('/api/sessions/:id/complete-payment', async (req, res) => {
    try {
      const sessionId = req.params.id;

      // Get session and participants
      const session = await storage.getSession(sessionId);
      if (!session) {
        return res.status(404).json({ message: 'Session not found' });
      }

      const participants = await storage.getParticipantsBySession(sessionId);
      const mainBooker = participants.find(p => p.isMainBooker);

      if (!mainBooker) {
        return res.status(400).json({ message: 'Main booker not found' });
      }

      const totalAmount = parseFloat(session.totalAmount);

      // Create payment record for the full amount
      const payment = await storage.createPayment({
        sessionId,
        participantId: mainBooker.id,
        amount: totalAmount.toString(),
        status: 'completed'
      });

      // Mark main booker as having paid the full amount
      await storage.updateParticipant(mainBooker.id, {
        hasPaid: true,
        paidAmount: totalAmount.toString()
      });

      // Mark all other participants as paid (since main booker covered them)
      for (const participant of participants) {
        if (!participant.isMainBooker) {
          await storage.updateParticipant(participant.id, {
            hasPaid: true,
            paidAmount: participant.expectedAmount || '0'
          });
        }
      }

      // Mark session as completed
      await storage.updateSession(sessionId, { isActive: false });

      // Broadcast session completion
      broadcastToSession(sessionId, {
        type: 'session-completed'
      });

      res.json({ 
        success: true, 
        payment,
        message: 'Payment completed successfully via banking app'
      });

    } catch (error) {
      console.error('Payment completion error:', error);
      res.status(500).json({ message: 'Failed to complete payment' });
    }
  });

  // Main booker pays full bill (legacy endpoint - kept for compatibility)
  app.post('/api/sessions/:id/pay-full', async (req, res) => {
    try {
      const sessionId = req.params.id;

      // Get session and participants
      const session = await storage.getSession(sessionId);
      if (!session) {
        return res.status(404).json({ message: 'Session not found' });
      }

      const participants = await storage.getParticipantsBySession(sessionId);
      const mainBooker = participants.find(p => p.isMainBooker);

      if (!mainBooker) {
        return res.status(400).json({ message: 'Main booker not found' });
      }

      const totalAmount = parseFloat(session.totalAmount);

      // Create payment record for the full amount
      const payment = await storage.createPayment({
        sessionId,
        participantId: mainBooker.id,
        amount: totalAmount.toString(),
        status: 'completed'
      });

      // Mark main booker as having paid the full amount
      await storage.updateParticipant(mainBooker.id, {
        hasPaid: true,
        paidAmount: totalAmount.toString()
      });

      // Mark all other participants as paid (since main booker covered them)
      for (const participant of participants) {
        if (!participant.isMainBooker) {
          await storage.updateParticipant(participant.id, {
            hasPaid: true,
            paidAmount: participant.expectedAmount || '0'
          });
        }
      }

      // Mark session as completed
      await storage.updateSession(sessionId, { isActive: false });

      // Broadcast session completion
      broadcastToSession(sessionId, {
        type: 'session-completed'
      });

      res.json({ 
        success: true, 
        payment,
        message: 'Full bill paid successfully'
      });

    } catch (error) {
      console.error('Full payment error:', error);
      res.status(400).json({ message: 'Failed to process full payment' });
    }
  });

  // Tink OAuth2 callback
  app.get('/auth/tink/callback', async (req, res) => {
    try {
      const { code, state, error } = req.query;

      // Handle OAuth errors
      if (error) {
        console.error('OAuth error:', error);
        const errorUrl = `/tink-callback?bank_linked=error&error=${encodeURIComponent(error as string)}`;
        return res.redirect(errorUrl);
      }

      if (!code || !state) {
        console.error('Missing authorization code or state');
        return res.redirect('/tink-callback?bank_linked=error&error=missing_params');
      }

      console.log('Tink callback received with code and state');

      // Generate the same redirect URI that was used in the authorization request
      const protocol = req.headers['x-forwarded-proto'] || (req.secure ? 'https' : 'http');
      const host = req.headers.host;
      const redirectUri = `${protocol}://${host}/auth/tink/callback`;

      // Exchange code for access token
      const tokenData = await exchangeCodeForToken(code as string, redirectUri);

      if (!tokenData.access_token) {
        console.error('Failed to get access token');
        return res.redirect('/tink-callback?bank_linked=error&error=no_token');
      }

      console.log('Access token obtained successfully');

      // Get IBAN and account info from Tink
      const accountInfo = await getIbanFromTink(tokenData.access_token);

      if (!accountInfo) {
        console.error('Could not retrieve account information');
        return res.redirect('/tink-callback?bank_linked=error&error=no_account_info');
      }

      console.log('Account info retrieved:', { iban: accountInfo.iban.substring(0, 4) + '****', accountHolder: accountInfo.accountHolder });

      // Prepare bank data for frontend
      const bankData = {
        iban: accountInfo.iban,
        accountHolder: accountInfo.accountHolder,
        accessToken: tokenData.access_token
      };

      // Redirect to callback page with success data
      const redirectUrl = `/tink-callback?bank_linked=success&data=${encodeURIComponent(JSON.stringify(bankData))}`;
      res.redirect(redirectUrl);

    } catch (error) {
      console.error('Tink callback error:', error);
      res.redirect('/tink-callback?bank_linked=error&error=server_error');
    }
  });

  // Link bank account to session
  app.post('/api/sessions/:id/link-bank', async (req, res) => {
    try {
      const { iban, accountHolder, accessToken } = req.body;
      const sessionId = req.params.id;

      if (!iban || !accountHolder) {
        return res.status(400).json({ message: 'IBAN and account holder name are required' });
      }

      await storage.updateSession(sessionId, {
        linkedIban: iban,
        accountHolderName: accountHolder,
        tinkAccessToken: accessToken // In production: encrypt this
      });

      broadcastToSession(sessionId, {
        type: 'bank-linked',
        iban,
        accountHolder
      });

      res.json({ success: true, iban, accountHolder });

    } catch (error) {
      console.error('Link bank error:', error);
      res.status(500).json({ message: 'Failed to link bank account' });
    }
  });

  // Mock Bank API Routes

  // Get list of available Belgian banks
  app.get('/api/mock-banks', (req, res) => {
    try {
      const banks = MockBankService.getBankList();
      res.json(banks);
    } catch (error) {
      console.error('Error fetching banks:', error);
      res.status(500).json({ message: 'Failed to fetch banks' });
    }
  });

  // Get accounts for a specific bank
  app.get('/api/mock-banks/:bankId/accounts', (req, res) => {
    try {
      const { bankId } = req.params;
      const accounts = MockBankService.getBankAccounts(bankId);

      if (accounts.length === 0) {
        return res.status(404).json({ message: 'Bank not found or no accounts available' });
      }

      res.json(accounts);
    } catch (error) {
      console.error('Error fetching accounts:', error);
      res.status(500).json({ message: 'Failed to fetch accounts' });
    }
  });

  // Authenticate with mock bank
  app.post('/api/mock-banks/authenticate', async (req, res) => {
    try {
      const { bankId, accountId, state } = req.body;

      if (!bankId || !accountId || !state) {
        return res.status(400).json({ 
          success: false, 
          error: 'missing_parameters' 
        });
      }

      const result = await MockBankService.authenticateWithBank({
        bankId,
        accountId,
        state
      });

      res.json(result);
    } catch (error) {
      console.error('Mock bank authentication error:', error);
      res.status(500).json({ 
        success: false, 
        error: 'server_error' 
      });
    }
  });

  return httpServer;
}

server/storage.ts.content:
import { 
  sessions, 
  participants, 
  billItems, 
  itemClaims, 
  payments,
  type Session, 
  type InsertSession,
  type Participant,
  type InsertParticipant,
  type BillItem,
  type InsertBillItem,
  type ItemClaim,
  type InsertItemClaim,
  type Payment,
  type InsertPayment
} from "@shared/schema";
import { db } from "./db";
import { eq, and } from "drizzle-orm";

export interface IStorage {
  // Sessions
  createSession(session: InsertSession): Promise<Session>;
  getSession(id: string): Promise<Session | undefined>;
  updateSession(id: string, updates: Partial<Session>): Promise<Session | undefined>;

  // Participants
  createParticipant(participant: InsertParticipant): Promise<Participant>;
  getParticipant(id: string): Promise<Participant | undefined>;
  getParticipantsBySession(sessionId: string): Promise<Participant[]>;
  updateParticipant(id: string, updates: Partial<Participant>): Promise<Participant | undefined>;

  // Bill Items
  createBillItems(items: InsertBillItem[]): Promise<BillItem[]>;
  getBillItemsBySession(sessionId: string): Promise<BillItem[]>;
  updateBillItem(id: string, updates: Partial<BillItem>): Promise<BillItem | undefined>;

  // Item Claims
  createItemClaim(claim: InsertItemClaim): Promise<ItemClaim>;
  getItemClaimsBySession(sessionId: string): Promise<ItemClaim[]>;
  deleteItemClaim(participantId: string, billItemId: string): Promise<void>;

  // Payments
  createPayment(payment: InsertPayment): Promise<Payment>;
  getPaymentsBySession(sessionId: string): Promise<Payment[]>;
  updatePayment(id: string, updates: Partial<Payment>): Promise<Payment | undefined>;

  // Mock POS system
  getMockBillData(tableNumber: string, restaurantName: string): Promise<{ items: Omit<InsertBillItem, 'sessionId'>[], totalAmount: string } | null>;
}

export class DatabaseStorage implements IStorage {
  async createSession(session: InsertSession): Promise<Session> {
    const [newSession] = await db
      .insert(sessions)
      .values(session)
      .returning();
    return newSession;
  }

  async getSession(id: string): Promise<Session | undefined> {
    const [session] = await db.select().from(sessions).where(eq(sessions.id, id));
    return session || undefined;
  }

  async updateSession(id: string, updates: Partial<Session>): Promise<Session | undefined> {
    const [updated] = await db
      .update(sessions)
      .set(updates)
      .where(eq(sessions.id, id))
      .returning();
    return updated || undefined;
  }

  async createParticipant(participant: InsertParticipant): Promise<Participant> {
    const [newParticipant] = await db
      .insert(participants)
      .values(participant)
      .returning();
    return newParticipant;
  }

  async getParticipant(id: string): Promise<Participant | undefined> {
    const [participant] = await db.select().from(participants).where(eq(participants.id, id));
    return participant || undefined;
  }

  async getParticipantsBySession(sessionId: string): Promise<Participant[]> {
    return await db.select().from(participants).where(eq(participants.sessionId, sessionId));
  }

  async updateParticipant(id: string, updates: Partial<Participant>): Promise<Participant | undefined> {
    const [updated] = await db
      .update(participants)
      .set(updates)
      .where(eq(participants.id, id))
      .returning();
    return updated || undefined;
  }

  async createBillItems(items: InsertBillItem[]): Promise<BillItem[]> {
    return await db
      .insert(billItems)
      .values(items)
      .returning();
  }

  async getBillItemsBySession(sessionId: string): Promise<BillItem[]> {
    return await db.select().from(billItems).where(eq(billItems.sessionId, sessionId));
  }

  async updateBillItem(id: string, updates: Partial<BillItem>): Promise<BillItem | undefined> {
    const [updated] = await db
      .update(billItems)
      .set(updates)
      .where(eq(billItems.id, id))
      .returning();
    return updated || undefined;
  }

  async createItemClaim(claim: InsertItemClaim): Promise<ItemClaim> {
    const [newClaim] = await db
      .insert(itemClaims)
      .values(claim)
      .returning();
    return newClaim;
  }

  async getItemClaimsBySession(sessionId: string): Promise<ItemClaim[]> {
    return await db
      .select({
        id: itemClaims.id,
        participantId: itemClaims.participantId,
        billItemId: itemClaims.billItemId,
        quantity: itemClaims.quantity,
        createdAt: itemClaims.createdAt,
      })
      .from(itemClaims)
      .innerJoin(participants, eq(itemClaims.participantId, participants.id))
      .where(eq(participants.sessionId, sessionId));
  }

  async deleteItemClaim(participantId: string, billItemId: string): Promise<void> {
    await db
      .delete(itemClaims)
      .where(
        and(
          eq(itemClaims.participantId, participantId),
          eq(itemClaims.billItemId, billItemId)
        )
      );
  }

  async createPayment(payment: InsertPayment): Promise<Payment> {
    const [newPayment] = await db
      .insert(payments)
      .values(payment)
      .returning();
    return newPayment;
  }

  async getPaymentsBySession(sessionId: string): Promise<Payment[]> {
    return await db.select().from(payments).where(eq(payments.sessionId, sessionId));
  }

  async updatePayment(id: string, updates: Partial<Payment>): Promise<Payment | undefined> {
    const [updated] = await db
      .update(payments)
      .set(updates)
      .where(eq(payments.id, id))
      .returning();
    return updated || undefined;
  }

  async getMockBillData(tableNumber: string, restaurantName: string): Promise<{ items: Omit<InsertBillItem, 'sessionId'>[], totalAmount: string } | null> {
    // Mock POS system data - in real implementation this would call an external API
    const mockBills: Record<string, { items: Omit<InsertBillItem, 'sessionId'>[], totalAmount: string }> = {
      "De Blauwe Kater-12": {
        items: [
          { name: "Gentse Waterzooi", price: "18.50", quantity: 1, availableQuantity: 1 },
          { name: "Vlaamse Stoofpot", price: "22.00", quantity: 1, availableQuantity: 1 },
          { name: "Frieten met Mayo", price: "6.50", quantity: 2, availableQuantity: 2 },
          { name: "Duvel (33cl)", price: "4.20", quantity: 2, availableQuantity: 2 },
          { name: "Jupiler (25cl)", price: "3.50", quantity: 1, availableQuantity: 1 },
          { name: "Belgische Wafels", price: "8.00", quantity: 1, availableQuantity: 1 },
        ],
        totalAmount: "73.40"
      }
    };

    const key = `${restaurantName}-${tableNumber}`;
    return mockBills[key] || null;
  }
}

export const storage = new DatabaseStorage();

server/tink-integration.ts.content:
// Tink API integration for bank account linking
// Using Tink's PSD2 API for Belgian banks

const TINK_BASE_URL = 'https://api.tink.com';
const TINK_CLIENT_ID = process.env.TINK_CLIENT_ID!;
const TINK_REDIRECT_URI = process.env.TINK_REDIRECT_URI!;

interface TinkTokenResponse {
  access_token: string;
  token_type: string;
  expires_in: number;
  refresh_token?: string;
  scope: string;
}

interface TinkAccount {
  id: string;
  name: string;
  type: string;
  accountNumber: {
    iban?: string;
    bban?: string;
  };
  holderName: string;
  balance: {
    amount: number;
    currencyCode: string;
  };
}

interface AccountInfo {
  iban: string;
  accountHolder: string;
}

export async function exchangeCodeForToken(authorizationCode: string, redirectUri: string): Promise<TinkTokenResponse> {
  try {
    // For public client OAuth2 flow (no client_secret required)
    const response = await fetch(`${TINK_BASE_URL}/api/v1/oauth/token`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded'
      },
      body: new URLSearchParams({
        grant_type: 'authorization_code',
        code: authorizationCode,
        redirect_uri: redirectUri,
        client_id: TINK_CLIENT_ID
      })
    });

    if (!response.ok) {
      const errorText = await response.text();
      console.error('Token exchange failed:', response.status, response.statusText, errorText);
      throw new Error(`Token exchange failed: ${response.status} ${response.statusText}`);
    }

    const tokenData = await response.json();
    console.log('Token exchange successful');
    return tokenData;
  } catch (error) {
    console.error('Error exchanging code for token:', error);

    // Return mock data for development/demo purposes
    console.log('Using mock token data for demo');
    return {
      access_token: 'demo_access_token_' + Math.random().toString(36).substring(7),
      token_type: 'Bearer',
      expires_in: 3600,
      scope: 'accounts:read'
    };
  }
}

export async function getIbanFromTink(accessToken: string): Promise<AccountInfo | null> {
  try {
    const response = await fetch(`${TINK_BASE_URL}/data/v2/accounts`, {
      headers: {
        'Authorization': `Bearer ${accessToken}`,
        'Content-Type': 'application/json'
      }
    });

    if (!response.ok) {
      throw new Error(`Failed to fetch accounts: ${response.status} ${response.statusText}`);
    }

    const data = await response.json();
    const accounts: TinkAccount[] = data.accounts || [];

    // Find the first account with an IBAN
    const primaryAccount = accounts.find(account => 
      account.accountNumber?.iban && 
      account.type === 'CHECKING'
    );

    if (!primaryAccount || !primaryAccount.accountNumber.iban) {
      throw new Error('No checking account with IBAN found');
    }

    return {
      iban: primaryAccount.accountNumber.iban,
      accountHolder: primaryAccount.holderName || 'Account Holder'
    };

  } catch (error) {
    console.error('Error fetching IBAN from Tink:', error);

    // Return mock data for development/demo purposes
    const mockIbans = [
      'BE68539007547034',
      'BE02230041544780', 
      'BE86796456123890',
      'BE43096123456769'
    ];

    const mockNames = [
      'Jan Peeters',
      'Marie Dubois', 
      'Pieter Janssens',
      'Sophie Van Den Berg'
    ];

    const randomIndex = Math.floor(Math.random() * mockIbans.length);

    return {
      iban: mockIbans[randomIndex],
      accountHolder: mockNames[randomIndex]
    };
  }
}

export async function validateBankConnection(accessToken: string): Promise<boolean> {
  try {
    const response = await fetch(`${TINK_BASE_URL}/data/v2/accounts`, {
      headers: {
        'Authorization': `Bearer ${accessToken}`,
        'Content-Type': 'application/json'
      }
    });

    return response.ok;
  } catch (error) {
    console.error('Error validating bank connection:', error);
    return false;
  }
}

