  --monarch-orange: #FFEDD5; /* Very light orange */
  --monarch-gray-400: #9CA3AF; /* Medium gray */
  --monarch-gray-300: #D1D5DB; /* Light gray */
  --monarch-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
  --monarch-shadow-lg: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
}

.dark {
  /* Monarch Money Dark Mode */
  --background: #1F2937; /* Dark background */
  --background-muted: #111827; /* Darker background */
  --foreground: #F9FAFB; /* Light text */
  --muted: #374151; /* Dark gray backgrounds */
  --muted-foreground: #9CA3AF; /* Medium gray text */
  --popover: #1F2937;
  --popover-foreground: #F9FAFB;
  --card: #1F2937;
  --card-foreground: #F9FAFB;
  --border: #374151; /* Dark borders */
  --input: #111827;
  --primary: #F97316; /* Keep vibrant colors */
  --primary-foreground: #FFFFFF;
  --secondary: #374151;
  --secondary-foreground: #F9FAFB;
  --accent: #FB923C;
  --accent-foreground: #FFFFFF;
  --destructive: #EF4444;
  --destructive-foreground: #FFFFFF;
  --ring: #F97316;

  /* Dark mode specific - Orange theme */
  --monarch-primary: #F97316;
  --monarch-purple: #FB923C;
  --monarch-green: #FED7AA;
  --monarch-orange: #FFEDD5;
  --monarch-gray-400: #9CA3AF;
  --monarch-gray-300: #6B7280;
  --monarch-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.3), 0 2px 4px -1px rgba(0, 0, 0, 0.2);
  --monarch-shadow-lg: 0 10px 15px -3px rgba(0, 0, 0, 0.4), 0 4px 6px -2px rgba(0, 0, 0, 0.3);
}

@layer base {
  * {
    @apply border-border;
  }

  html {
    font-family: system-ui, -apple-system, "SF Pro Display", sans-serif;
    height: 100%;
    height: -webkit-fill-available;
    /* Performance optimizations */
    text-rendering: optimizeLegibility;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
  }

  body {
    @apply antialiased text-foreground;
    font-family: system-ui, -apple-system, "SF Pro Display", sans-serif;
    background: url('@assets/a76a0c1a-43da-4af1-92f3-6326f9b4de38_1754313248853.jpg') no-repeat center center fixed;
    background-size: cover;
    background-attachment: fixed;
    margin: 0;
    padding: 0;
    min-height: 100vh;
    min-height: -webkit-fill-available;
    overflow-x: hidden;
    /* Performance optimizations */
    will-change: transform;
    backface-visibility: hidden;
    transform: translateZ(0);
  }

  #root {
    min-height: 100vh;
    min-height: -webkit-fill-available;
    display: flex;
    flex-direction: column;
  }
}

@layer utilities {
  .font-sf {
    font-family: system-ui, -apple-system, "SF Pro Display", sans-serif;
  }

  .active-scale-98:active {
    transform: scale(0.98);
  }

  /* Monarch iOS Widget Cards - Mobile Optimized */
  .monarch-widget {
    @apply bg-white rounded-xl p-3 transition-all duration-200;
    box-shadow: var(--monarch-shadow);
  }

  .monarch-widget:hover {
    box-shadow: var(--monarch-shadow-lg);
    transform: translateY(-1px);
  }

  .monarch-card {
    @apply bg-white rounded-xl p-3 border border-gray-200 transition-all duration-200;
  }

  .monarch-card:hover {
    @apply shadow-md;
    transform: translateY(-1px);
  }

  /* Monarch iOS Buttons - Mobile Optimized */
  .monarch-btn {
    @apply px-4 py-2.5 rounded-xl font-semibold text-sm transition-all duration-200;
    box-shadow: var(--monarch-shadow);
  }

  .monarch-btn:active {
    transform: scale(0.98);
  }

  .monarch-btn-primary {
    @apply text-white;
    background: var(--monarch-primary);
  }

  .monarch-btn-primary:hover {
    background: #EA580C;
  }

  .monarch-btn-secondary {
    @apply bg-gray-100 text-gray-900;
  }

  .monarch-btn-secondary:hover {
    @apply bg-gray-200;
  }

  /* Icon Buttons */
  .monarch-icon-btn {
    @apply w-10 h-10 rounded-full flex items-center justify-center transition-all duration-150;
  }

  .monarch-icon-btn:hover {
    @apply bg-gray-100;
  }

  /* Monarch Typography - Mobile Optimized */
  .monarch-title {
    @apply text-lg font-semibold text-gray-900;
  }

  .monarch-section-title {
    @apply text-sm uppercase tracking-wide text-gray-500 font-medium;
  }

  .monarch-body {
    @apply text-sm text-gray-600 leading-relaxed;
  }

  .monarch-caption {
    @apply text-sm text-gray-500;
  }

  /* Legacy Support - Keep old classes working during transition */
  .parti-card {
    @apply monarch-card;
  }

  .parti-card-elevated {
    @apply monarch-widget;
  }

  .parti-button {
    @apply monarch-btn;
  }

  .parti-button-primary {
    @apply monarch-btn-primary;
  }

  .parti-button-secondary {
    @apply monarch-btn-secondary;
  }

  /* Custom scrollbar for mobile */
  .custom-scrollbar::-webkit-scrollbar {
    width: 4px;
  }

  .custom-scrollbar::-webkit-scrollbar-track {
    background: hsl(var(--muted));
  }

  .custom-scrollbar::-webkit-scrollbar-thumb {
    background: hsl(var(--primary));
    border-radius: 2px;
  }

  .full-viewport {
    min-height: 100vh;
    min-height: -webkit-fill-available;
  }
}

/* PWA specific styles */
@media (display-mode: standalone) {
  body {
    -webkit-user-select: none;
    -webkit-touch-callout: none;
    -webkit-tap-highlight-color: transparent;
  }
}

/* Mobile optimizations - iPhone 15 Specific */
@media (max-width: 768px) {
  .max-w-sm {
    max-width: 100%;
  }

  /* Ensure full viewport height on mobile */
  .min-h-screen {
    min-height: 100vh;
    min-height: -webkit-fill-available;
  }

  .parti-container {
    width: 100vw;
    min-height: 100vh;
    min-height: -webkit-fill-available;
    padding: 0;
    margin: 0;
    background: var(--background-muted);
  }

  .monarch-container {
    @apply min-h-screen;
    background: var(--background-muted);
  }

  /* Mobile specific compact styles */
  .monarch-widget {
    @apply p-2 rounded-lg;
  }

  .monarch-card {
    @apply p-2.5 rounded-lg;
  }

  .monarch-btn {
    @apply px-3 py-2 text-xs rounded-lg;
  }

  .monarch-title {
    @apply text-base;
  }

  .monarch-body {
    @apply text-xs;
  }

  .monarch-caption {
    @apply text-xs;
  }
}

/* Touch-friendly interactions - Mobile Optimized */
button, .button, [role="button"] {
  -webkit-tap-highlight-color: transparent;
  touch-action: manipulation;
  min-height: 40px;
  min-width: 40px;
}

/* Enhanced touch targets - Mobile Optimized */
.touch-target {
  min-height: 40px;
  min-width: 40px;
  padding: 8px 12px;
}

/* Smooth animations for better UX */
.animate-slide-up {
  animation: slideUp 0.3s ease-out;
}

@keyframes slideUp {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.animate-fade-in {
  animation: fadeIn 0.2s ease-out;
}

@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

/* Loading animation */
@keyframes spin {
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
}

.animate-spin {
  animation: spin 1s linear infinite;
}

/* QR Scanner with neutral colors */
.qr-scanner-overlay {
  background: linear-gradient(
    45deg,
    transparent 30%,
    rgba(0, 0, 0, 0.05) 30%,
    rgba(0, 0, 0, 0.05) 70%,
    transparent 70%
  );
  animation: scan-line 2s linear infinite;
}

/* Monarch Money utilities */
.parti-surface-muted {
  background: var(--parti-surface-muted);
}

/* Monarch Color Utilities */
.text-monarch-primary {
  color: var(--monarch-primary);
}

.text-monarch-purple {
  color: var(--monarch-purple);
}

.text-monarch-green {
  color: var(--monarch-green);
}

.text-monarch-orange {
  color: var(--monarch-orange);
}

.bg-monarch-primary {
  background: var(--monarch-primary);
}

.bg-monarch-purple {
  background: var(--monarch-purple);
}

.bg-monarch-green {
  background: var(--monarch-green);
}

.bg-monarch-orange {
  background: var(--monarch-orange);
}

/* Legacy Support */
.parti-text-primary {
  color: var(--monarch-primary);
}

.parti-text-accent {
  color: var(--monarch-green);
}

.parti-bg-primary {
  background: var(--monarch-primary);
}

.parti-bg-accent {
  background: var(--monarch-green);
}

/* Monarch iOS style inputs */
.monarch-input {
  @apply rounded-2xl border border-gray-200 bg-white px-4 py-3 text-base transition-all;
}

.monarch-input:focus {
  outline: none;
  border-color: var(--monarch-primary);
  box-shadow: 0 0 0 2px rgba(99, 102, 241, 0.1);
}

/* Legacy input support */
.parti-input {
  @apply monarch-input;
}

/* Legacy Typography - Maps to Monarch styles */
.parti-heading-1 {
  @apply monarch-title mb-4;
}

.parti-heading-2 {
  @apply text-lg font-semibold text-gray-900 mb-3;
}

.parti-heading-3 {
  @apply text-base font-semibold text-gray-900 mb-2;
}

.parti-body {
  @apply monarch-body;
}

.parti-body-semibold {
  @apply text-base text-gray-900 leading-relaxed font-semibold;
}

.parti-small {
  @apply monarch-caption;
}

.parti-step-indicator {
  @apply text-base font-medium text-gray-500;
}

.parti-amount {
  @apply font-semibold tabular-nums text-gray-900;
}

.parti-amount-large {
  @apply text-2xl font-bold tabular-nums text-gray-900;
}

@keyframes scan-line {
  0% { background-position: 0% 0%; }
  100% { background-position: 100% 100%; }
}

/* Super fast performance optimizations */
* {
  /* Hardware acceleration for all elements */
  -webkit-transform: translateZ(0);
  -moz-transform: translateZ(0);
  -ms-transform: translateZ(0);
  -o-transform: translateZ(0);
  transform: translateZ(0);
}

/* Optimize animations for 60fps */
@keyframes fade-in {
  from { opacity: 0; }
  to { opacity: 1; }
}

@keyframes slide-up {
  from { 
    opacity: 0; 
    transform: translateY(10px) translateZ(0); 
  }
  to { 
    opacity: 1; 
    transform: translateY(0) translateZ(0); 
  }
}

.animate-fade-in {
  animation: fade-in 0.15s ease-out forwards;
}

.animate-slide-up {
  animation: slide-up 0.2s ease-out forwards;
}

/* Fast transitions */
.monarch-btn, .parti-btn {
  transition: all 0.1s ease-out !important;
}

.monarch-card, .parti-card {
  transition: transform 0.1s ease-out, box-shadow 0.1s ease-out !important;
}

/* Reduce motion for users who prefer it */
@media (prefers-reduced-motion: reduce) {
  *,
  *::before,
  *::after {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
    scroll-behavior: auto !important;
  }
}

